def format_numbers(numbers):
    formatted_numbers = [numbers[i:i+2] for i in range(0, len(numbers), 2)]
    formatted_numbers = [f"0x{num}" for num in formatted_numbers]
    result = ",".join(formatted_numbers)
    return result

